<script>
    $(document).ready(function(){
        works = [];
        $(".workItem").click(function()
        {
            let cname = $(this).attr("title");
            let wicons = $("[title='"+cname+"']");
            let wicon1 = $(wicons).children("i.main");
            let wicon2 = $(wicons).children("svg.main");

            let sideIcon = $(wicons).children(".fa-check");

            let wicon;

            if(wicon1.length===0)
                wicon = wicon2;
            else
                wicon = wicon1;

            if(wicon.attr("class").indexOf("selected")>-1)
            {
                $(sideIcon).hide();
                $(wicon).show(100);
                $(wicons).css({"border-color":"#3c3131","backgroundColor":"ghostwhite"});
                $(wicon).removeClass("selected");

                // Finding The Work Element To Remove It
                let windex = works.indexOf(cname);
                if(windex !== -1)
                {
                    for(i=windex;i<works.length;i++)
                    {
                        works[i] = works[i+1];
                    }
                }
            }
            else
            {
                $(wicon).hide(50);
                $(sideIcon).show(0);
                $(wicon).addClass("selected");
                $(wicons).css({"border-color":"chocolate","backgroundColor":"rgba(210,105,30,0.25)"});

                // Adding Work Into The Array
                works[works.length] = cname;
            }
        });
        $("#selectWorks").click(function()
        {
            let len = 0;
            for(i=0;i<works.length;i++)
            {
                if(!(works[i]===undefined || works[i]===null))
                    len++;
            }

            if(len===0)
            {
                $(".manual-invalid-feedback").css({"display":"block","clear":"both","text-align":"center","font-size":"1.1rem"});
                alert("Please Select The Works You Do To Continue.");
            }
            else
            {
                $.post("<?php echo(htmlspecialchars($_SERVER['PHP_SELF'])); ?>",{works:works}, function (data, status) {
                    document.write(data);
                    // Server's Responses
                });
            }
        });
    })
</script>

